
public class Exercise1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		long x;
		
		x = 100;
		System.out.println(x);
		x = x + 10; //Line A
		System.out.println(x);
		
		x = x + 20; //Line B
		System.out.println(x+ "\n");
		/* the first result is 
		  100 
		  110
		  130*/

		long y;
		
		y = 100;
		System.out.println(y);
		y += 10; //Line A
		System.out.println(y);
		
		y += 20; //Line B
		System.out.println(y);
		
		
		/* The ouput of the result is also 
		  100
		  110
		  130*/
		 
		
	}

}
